import React from 'react'
import { connect } from 'react-redux'

import Profile from '../Profile'

import { Progress } from 'react-sweet-progress'
import 'react-sweet-progress/lib/style.css'

class SleepEfficiency extends React.Component {

  componentDidUpdate(prevProps) {
    const userToken = localStorage.getItem('token')

    if (this.props.date !== prevProps.date) {
      this.props.sleep(this.props.date, userToken)
    }
  }

  render() {
    let efficiency
    let hours_slept

    if (this.props.sleepInfo.sleep) {
      console.log(this.props.sleepInfo)
      efficiency = this.props.sleepInfo.sleep[0].efficiency

      // round to one decimal place
      hours_slept = Math.round((this.props.sleepInfo.sleep[0].duration / 3600000) * 10 / 10)
    }

    return (
      <div className='progress-circle'>
        <Progress
          type='circle'
          width={160}
          strokeWidth={13}
          percent={efficiency}
          theme={{
            active: {
              trailColor: 'rgb(236,236,236)',
              color: 'rgba(133,207,218)'
            }
          }}
        />
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  // debugger
  return {
    sleepInfo: state.user.sleep
  }
}

export default connect(mapStateToProps)(SleepEfficiency)

