import React from 'react'
import { Form, Container, Grid } from 'semantic-ui-react'

class Goal extends React.Component {

  handleEdit = () => {

    this.props.history.replace(`/my_goals/edit/${this.props.goal.id}`)
  }

  render() {
    return (
      <div className='grid-container'>
        <div className='grid-item'>
          <h4>Goal Date:</h4> {this.props.goal.goalDate}
          <h4>Bedtime Target:</h4> {this.props.goal.bedtimeTarget}
          <h4>Wakeup Time Target:</h4> {this.props.goal.wakeupTarget}

          <br />

          <button type='button' onClick={this.handleEdit}>edit</button>
        </div>
      </div>
    )
  }
}

export default Goal
