import React from 'react'
import EditGoal from './EditGoal'

class Goal extends React.Component {

  state = {
    renderGoal: ''
  }

  handleEdit = () => {
    this.props.props.history.replace(`/my_goals/edit/${this.props.goal.id}`)

    this.setState({
      renderGoal: <EditGoal props={this.props.props} goal={this.props.goal} handleEdit={this.handleEdit} />
    })
  }

  render() {
    console.log(this.state.renderGoal)
    return (
      this.state.renderGoal
    )
  }
}

export default Goal
