import React from 'react'

import DatePicker from 'react-datepicker'
import '../../../node_modules/react-datepicker/dist/react-datepicker.css'

class SleepForm extends React.Component {

  state = {
    startDate: new Date(),
    goalDate: '',
    bedtimeTarget: '',
    wakeupTime: ''
  }

  handleChange = (e, date) => {
    // debugger
    console.log(e)

    this.setState({
      startDate: date,
      goalDate: e.toString().split(' ').slice(0, 4).join(' ')
    })
  }

  handleBedtimeChange = (e) => {

    console.log('BED TIME')
    let militaryHour = Number(e.toString().split(' ')[4].split(':')[0])
    let minutes = e.toString().split(' ')[4].split(':')[1]

    if (militaryHour > 12) {
      militaryHour -= 12
    }

    this.setState({
      bedtimeTarget: militaryHour.toString() + ':' + minutes
    })
  }

  handleWaketimeChange = (e) => {

    console.log('WAKE UP')
    let militaryHour = Number(e.toString().split(' ')[4].split(':')[0])
    let minutes = e.toString().split(' ')[4].split(':')[1]

    if (militaryHour > 12) {
      militaryHour -= 12
    }

    this.setState({
      wakeupTime: militaryHour.toString() + ':' + minutes
    })
  }

  render() {
    return (
      <div className='datetime'>

        <label>
          Goal Date:
            <DatePicker
              selected={this.state.startDate}
              onChange={e => this.handleChange(e)}
            />
        </label>

        <br />

        <label>
          Bedtime Target:
            <DatePicker
              selected={this.state.startDate}
              onChange={e => this.handleBedtimeChange(e)}
              showTimeSelect
              showTimeSelectOnly
              timeIntervals={15}
              dateFormat="h:mm aa"
              timeCaption="Time"
            />
        </label>

        <br />

        <label>
          Wakeup Target:
            <DatePicker
              selected={this.state.startDate}
              onChange={e => this.handleWaketimeChange(e)}
              showTimeSelect
              showTimeSelectOnly
              timeIntervals={15}
              dateFormat="h:mm aa"
              timeCaption="Time"
            />
        </label>

      </div>
    )
  }
}


export default SleepForm
