import React from 'react'
import { Modal, Form, Button, Icon } from 'semantic-ui-react'

class ModalManager extends React.Component {

  state = {
    something: '',
    showModal: false
  }

  handleChangeForms = (e, { value }) => {
    this.setState({
      something: value
    })
  }

  handleCreateButton(e) {
    e.preventDefault()

    this.closeModal()
  }

  closeModal = () => {
    this.setState({
      showModal: false
    })
  }

  render() {
    const {
      something,
      showModal
    } = this.state

    return (
      <Modal closeIcon onClose={this.closeModal} open={showModal} trigger={<span onClick={() => this.setState({ showModal: true })}>Add Sleep Goal</span>}>
        <Modal.Header>Add Sleep Goal</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Input
              label='Something'
              value={something}
              onChange={this.handleChangeForms}
            />
            <Button onClick={(evt) => this.handleCreateButton(evt)}>Save</Button>
          </Form>
        </Modal.Content>
      </Modal>
    )
  }
}

export default ModalManager
