const { Component, PropTypes } = React
const { render } = ReactDOM
const { Motion, spring, presets } = ReactMotion

class HamburgerButton extends Component {
  constructor() {
    super()

    this.state = {
      toggle: false
    }
  }

  handleClick() {
    this.setState({toggle: !this.state.toggle})
  }

  render() {
    const style = {
      overflow: 'visible',
      cursor: 'pointer',
      // disable touch highlighting on devices
      WebkitTapHighlightColor: "rgba(0,0,0,0)",
    }

    return (
      <svg
        viewBox="0 0 96 96"
        height="1em"
        onClick={this.handleClick.bind(this)}
        style={style}
      >
        <Motion
          style={{
            x: spring(this.state.toggle ? 1 : 0, presets.wobbly ),
            y: spring(this.state.toggle ? 0: 1, presets.wobbly ),
          }}
        >
          {({ x, y }) =>
            <g
              id="navicon"
              fill="none"
              stroke="currentColor"
              strokeWidth="14"
              strokeLinecap="round"
              strokeLinejoin="round"
             >
              <line
                transform={`translate(${x * 12}, ${x * -7}) rotate(${x * 45}, 7, 26)`}
                x1="7" y1="26" x2="89" y2="26"
               />
              <line
                transform={`translate(${x * 12}, ${x * 7}) rotate(${x * -45}, 7, 70)`}
                x1="7" y1="70" x2="89" y2="70"
               />
              <line
                transform={`translate(${x * -96})`}
                opacity={y}
                x1="7" y1="48" x2="89" y2="48"
               />
            </g>
          }
        </Motion>
      </svg>
    )
  }
}

render(
  <HamburgerButton />,
  document.getElementById('render')
)
