import React from 'react'
import { connect } from 'react-redux'
import { fetchHeartRate } from '../../actionCreators/userActions'

class Heartrate extends React.Component {

  componentDidMount() {
    const userToken = localStorage.getItem('token')

    this.props.getHeartRate(userToken)
  }

  render() {
    console.log(this.props.heartRate)
    return (
      <div className='bpm'>
        {this.props.heartRate['activities-heart'] ? this.props.heartRate['activities-heart'][0].restingHeartRate + ' bpm' : null}
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    token: state.token,
    heartRate: state.heartRate
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    getHeartRate: (access_token) => dispatch(fetchHeartRate(access_token))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Heartrate)
