import React from 'react'
import { connect } from 'react-redux'

import DatePicker from 'react-datepicker'
import '../../../node_modules/react-datepicker/dist/react-datepicker.css'

class SleepForm extends React.Component {

  state = {
    startDate: new Date()
    // goalDate: '',
    // bedtimeTarget: '',
    // wakeupTarget: ''
  }

  handleChange = (e, date) => {
    // debugger

    this.setState({
      startDate: date
    })

    return e.toString().split(' ').slice(0, 4).join(' ')
  }

  handleBedtimeChange = (e) => {

    console.log('BED TIME')
    let militaryHour = Number(e.toString().split(' ')[4].split(':')[0])
    let minutes = e.toString().split(' ')[4].split(':')[1]

    if (militaryHour > 12) {
      militaryHour -= 12
    }

    return militaryHour.toString() + ':' + minutes

    // this.setState({
    //   bedtimeTarget: militaryHour.toString() + ':' + minutes
    // })
  }

  handleWaketimeChange = (e) => {

    console.log('WAKE UP')
    let militaryHour = Number(e.toString().split(' ')[4].split(':')[0])
    let minutes = e.toString().split(' ')[4].split(':')[1]

    if (militaryHour > 12) {
      militaryHour -= 12
    }

    return militaryHour.toString() + ':' + minutes
    // this.setState({
    //   wakeupTime: militaryHour.toString() + ':' + minutes
    // })
  }

  handleSubmit = (e) => {
    e.preventDefault()

    console.log(e)

    // this.props.fetchData()

    // fetch('http://localhost:3000/api/v1/goals', {
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': 'application/json',
    //     'Authorization': 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIyMkRGQ0wiLCJzdWIiOiI3MkNWUzMiLCJpc3MiOiJGaXRiaXQiLCJ0eXAiOiJhY2Nlc3NfdG9rZW4iLCJzY29wZXMiOiJyd2VpIHJhY3QgcmhyIHJwcm8gcnNsZSIsImV4cCI6MTU0NTY4NTc5OCwiaWF0IjoxNTQ1MDgwOTk4fQ.GHg743AtpdKvg6Eu6rPB-VxVFV1_C-zxpQklv9KyGAU'
    //   },
    //   body: JSON.stringify({
    //     goalDate: '2018-12-20',
    //     bedtimeTarget: '11:00 PM',
    //     wakeupTarget: '6:00 AM'
    //   })
    // })
    //   .then(res => res.json())
    //   .then(console.log)
  }

  render() {
    return (
      <div className='datetime'>

        <label>
          <h4>Goal Date:</h4>
            <form onSubmit={e => this.handleSubmit(e)}>
              <DatePicker
                selected={this.state.startDate}
                onChange={e => this.handleChange(e)}
              />
              <input type='submit'/>
            </form>

        </label>

        <br /><br />

        <label>
          <h4>Bedtime Target:</h4>
            <form onSubmit={e => this.handleSubmit(e)}>
              <DatePicker
                selected={this.state.startDate}
                onChange={e => this.handleBedtimeChange(e)}
                showTimeSelect
                showTimeSelectOnly
                timeIntervals={15}
                dateFormat="h:mm aa"
                timeCaption="Time"
              />
              <input type='submit'/>
            </form>
        </label>

        <br /><br />

        <label>
          <h4>Wakeup Target:</h4>
            <form onSubmit={e => this.handleSubmit(e)}>
              <DatePicker
                selected={this.state.startDate}
                onChange={e => this.handleWaketimeChange(e)}
                showTimeSelect
                showTimeSelectOnly
                timeIntervals={15}
                dateFormat="h:mm aa"
                timeCaption="Time"
              />
              <input type='submit'/>
            </form>
        </label>

      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    goalDate: state.goalDate,
    wakeupTarget: state.wakeupTarget,
    bedtimeTarget: state.bedtimeTarget
  }
}

const mapDispatchToProps = dispatch => {
  return {
    fetchData: (goalDate, bedtimeTarget, wakeupTarget, token) => dispatch(this.fetchGoalDate(goalDate, bedtimeTarget, wakeupTarget, token))
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(SleepForm)
