import React from 'react'

const Modal = ({ handleClose, show, children }) => {

  const showModal = show ? 'block' : 'none'

  return (
    <div className={showModal}>
      <section className="modal">
        {children}
        <button onClick={handleClose}>close</button>
      </section>
    </div>
  )
}

export default Modal
