import React from 'react'
import { connect } from 'react-redux'
import { Container, Header, Button } from 'semantic-ui-react'
import { NavLink } from 'react-router-dom'

import ModalManager from './ModalManager'
import { fetchUserData, openModal } from '../store/actionCreators/userActions'

class Profile extends React.Component {

  componentDidMount() {
    const userToken = localStorage.getItem('token')

    if (userToken) {
      this.props.getUser('https://api.fitbit.com/1/user/-/profile.json', userToken)
    }
  }

  render() {
    return (
      <div className='profile'>
      <Container>
      <Header block size="huge">
        <Header.Content>semantic-ui-modal-redux</Header.Content>
        <Header.Subheader>It's alive</Header.Subheader>
      </Header>
      <ModalManager />
      <div>Our content will go here.</div>
      <Button onClick={() => this.props.openModal({
            header: "Test content",
            content: "Test content 2"
          })}>
        Open modal
      </Button>
    </Container>

        <span id='identity'>
          <span id='name'>{this.props.user.user ? this.props.user.user.firstName : null}</span>
          {this.props.user.user ? <img class='ui massive circular image' id='photo' src={this.props.user.user.avatar} alt='profile pic' /> : null}

          <span id='map'>
            <i class="map marker alternate icon"></i>
            {this.props.user.user ? this.props.user.user.timezone : null}
          </span>
        </span>

        <span id='biometrics'>
          {this.props.user.user ? this.props.user.user.height + ' cm ': null}

          {this.props.user.user ? this.props.user.user.weight + ' kg' : null}

          <span className='profile-link'>
            <i class="star outline icon"></i><span id='add_goal'>Add Sleep Goal</span>
          </span>

          <span className='profile-link'>
            <i class="chart bar outline icon"></i><NavLink to='/sleep_stats' className='navlink'>Statistics</NavLink>
          </span>
        </span>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    user: state.user.user
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    getUser: (url, access_token) => dispatch(fetchUserData(url, access_token))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Profile)


