import React from 'react'

class MenuContainer extends React.Component {

  state = {
    show: false
  }

  toggleMenu = () => {
    this.setState({
      show: !this.state.show
    })
  }

  handleMouseDown = (e) => {
    this.toggleMenu

    console.log('clicked')
    e.stopPropagation()
  }

  render() {
    return (
      <div className='menu-container'>
        MenuContainer
      </div>
    )
  }
}

export default MenuContainer
