import React from 'react'
import { Modal, Form, Button } from 'semantic-ui-react'

import GoalForm from './goals/GoalForm'
import '../styling/ModalManager.css'

class ModalManager extends React.Component {

  state = {
    showModal: false
  }

  handleClose = (e) => {
    e.preventDefault()

    this.closeModal()
  }

  closeModal = () => {
    this.setState({
      showModal: false
    })
  }

  render() {
    const style = {
      content: {
        border: '0',
        borderRadius: '4px',
        bottom: 'auto',
        height: '7rem',  // set height
        left: '50%',
        padding: '2rem',
        position: 'fixed',
        right: 'auto',
        top: '50%', // start from center
        transform: 'translate(-50%,-' + offsetPx + ')', // adjust top "up" based on height
        width: '40%',
        maxWidth: '40rem'
      }
    }

    const { showModal } = this.state

    return (
      <Modal className='ui tiny modal' style={style} closeIcon onClose={this.closeModal} open={showModal}
      trigger={<span onClick={() => this.setState({ showModal: true })}>Add Sleep Goal</span>}>
        <Modal.Header id='header'>Add Sleep Goal</Modal.Header>
        <Modal.Content id='content'>
          <GoalForm handleClose={this.handleClose} />
        </Modal.Content>
      </Modal>
    )
  }
}

export default ModalManager
