import React from 'react'
import { connect } from "react-redux"
import { fetchUserData } from "../store/actionCreators/userActions"

import MenuContainer from './menu/MenuContainer'

class Home extends React.Component {

  componentDidMount() {
    const userToken = localStorage.getItem('token')

    if (userToken && this.props.date !== '') {
      this.props.getUser('https://api.fitbit.com/1/user/-/profile.json', this.props.date, userToken)
    }
  }

  componentDidUpdate(prevProps) {
    const userToken = localStorage.getItem('token')
    debugger

    if (this.props.date !== prevProps.date) {
      this.props.getUser('https://api.fitbit.com/1/user/-/profile.json', this.props.date, userToken)
    }
  }

  render() {
    return (
      <div className='welcome'>
        <MenuContainer />
        <div className='title'>
          <span id='title-1'>SLEEPTRACKER</span>
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    date: state.user.date
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    getUser: (url, date, access_token) => dispatch(fetchUserData(url, date, access_token))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Home)
